<?phpinclude_once "../../includes/sessao.php";$tipo 	  	= verifString($_GET['tipo']);$AlimentoID = verifString($_GET['AlimentoID']);$MedidaID 	= verifString($_GET['MedidaID']);$DioceseID 	= verifString($_GET['DioceseID']);$alimento 	= verifString($_GET['alimento']);$medida 	= verifString($_GET['medida']);//$tipo = "alimento";//$AlimentoID = 2;	switch($tipo)	{		case 'alimento' :			$sql = "SELECT * FROM alimentos order by Nome";			$option = 'selecione o alimento';			$string = 'ID';			break;		case 'medida' :			$sql = "SELECT * FROM alimentos_medidas where AlimentoID=".$AlimentoID." order by Nome";			$option = 'Grama';			$string = 'ID';						break;		case 'diocese' :			$sql = "SELECT * FROM dioceses where AlimentoID=".$AlimentoID." order by Nome";				$option = 'a diocese';			$string = 'ID';						break;				case 'medidaString' :			$sql = "SELECT A.* FROM medidas A, alimentos B where A.AlimentoID=B.AlimentoID and B.Sigla='".$alimento."' order by A.Nome";			$option = 'a medida';			$string = 'ID';						break;				}		/* Executamos o SQL aqui */	//echo $sql;	$consulta = mysql_query($sql);		/* Iniciamos o resultado como null */	$resultado = null;	/* O primeiro option que vai aparecer já com o titulo dinâmico a partir de $option  */	$resultado = "<option value='0'>$option</option>";		/* Buscamos os resultados usando $c->string para identificar o ID dinamico pois varia, no caso temos iso3, uf, nome e nome  */	while($row = mysql_fetch_array($consulta) ) {		$flag = "";		/* alimento em edicao*/		if ($tipo=='alimento' and $AlimentoID){			$sqlCombo2 = "select * from alimentos where ID='$AlimentoID' and ID=$row[ID]";			$buscaalimento = mysql_query($sqlCombo2);			if (mysql_fetch_row($buscaalimento)){				$flag = "selected";			}					}				/* medida em edicao*/		if ($tipo=='medida' and $AlimentoID and $MedidaID){			$buscamedida = mysql_query("select * from alimentos_medidas where ID=$MedidaID and ID=$row[ID]");			if (mysql_fetch_row($buscamedida)){				$flag = "selected";			}				}				if($tipo=='medida'){			$resultado .= "<option value='$row[$string]' $flag>$row[Nome] (".number_format($row[Quantidade],0,'','')."g)</option>";		}else{			$resultado .= "<option value='$row[$string]' $flag>$row[Nome]</option>";		}			}			/* Imprimimos o resultado */	print $resultado;	?>	